$(document).addEvent("domready",function(){var a=$("backend_admin_menu");if(a==null){return}a.getElements("li.label").each(function(b){var c=b.getElement("ul.actions");c.set("tween",{duration:200});b.addEvents({mouseenter:function(){$("header").setStyle("border-bottom-left-radius","0");c.fade("in")},mouseleave:function(){c.fade("out");$("header").setStyle("border-bottom-left-radius","3px")}})})});